# Combien y a-t-il de handle dans cette array ?
def length_handle(handle)
  puts "Il y a #{handle.length()} personnes"
  return handle.length
end

# Quel est le handle le plus court de cette liste ?
def short_handle(handle)
  print "Le handle le plus court de cette liste est : "
  return handle.min_by {|x| x.size}.length
end

# Combien y-a-t'il de handle contenant 5 caractères (le @ ne compte pas pour un caractère)
def cinq(journalists)
  handle_5 = journalists.select {|journalists| journalists.length == 6}.length
  puts "Il y a #{handle_5} balzes qui ont 5 caractères"
end

#Combien commencent par une majuscule (première lettre juste après le @)?
def only_capitals(handle)
  capital = handle.grep(/@[A-Z]/).length
  puts "#{capital} blaze commencent par une majuscule" 
end

# Trie la liste de handle par ordre alphabétique.
def alphabetically(handle)
  ordre = handle.sort_by {|handle| handle.downcase}
  puts ordre
end

# Trie la liste de handle par taille des handle (les plus petits en premiers, les plus grands après)
def size(handle)
  handle = handle.sort_by {|x| x.length}
  puts handle
end

# Quelle est la position dans l'array de la personne @epenser ?
def epenser(handle)
  position = handle.find_index("@epenser")
  puts position
end

# Sors-moi une répartition des handle par taille de ces derniers (nombre de handle avec 1 caractère, nombre de handle avec 2 caractères, nombre de handle avec 3 caractères, etc)
def long_handle(handle)
  print "Le handle le plus long de cette liste est : "
  return handle.max_by {|x| x.size}.length
end

def la_boucle(handle, max)
  (max+1).times do |i|
   list_size = handle.select{|handle| handle.length == i} 
   new_list_size = list_size.join(',')
    if list_size != []
    puts "TAILLE DE #{i} CARACTERE :" 
    puts "#{new_list_size}"
    puts " "
    end
  end
end

